import { Link } from '@govie-react/ds';
import { Fragment } from 'react';

function LargeButton({
  href,
  label,
  children,
}: {
  href: string;
  label: string;
  children: React.ReactNode;
}) {
  return (
    <div className="flex flex-col gap-md items-center justify-center bg-gray-50 px-md py-xl">
      {children}
      <div>
        <Link href={href}>{label}</Link>
      </div>
    </div>
  );
}

